#include "core_cm3.h"
#include "GPIO_DRIVER/gpio.h"
#include "EXTI_DRIVER/ext_interrupt.h"

#define TaskA_stack_sz 100
#define TaskB_stack_sz 100

#define OS_SET_PSP(add)			__asm volatile("mov r0, %0 \n\t msr PSP, r0 \n\t" : : "r" (add))
#define OS_SWITCH_SP_TO_PSP		__asm volatile(" mrs r0, CONTROL \n\t mov r1, #0x02 \n\t orr r0, r0, r1 \n\t msr CONTROL, r0 \n\t")
#define OS_SWITCH_SP_TO_MSP		__asm volatile(" mrs r0, CONTROL \n\t mov r1, #0x05 \n\t and r0, r0, r1 \n\t msr CONTROL, r0 \n\t")
#define OS_GENERATE_EXCEPTION 	__asm volatile("SVC #0x3")

extern int _estack;

//MAIN STACK
unsigned int _S_MSP = &_estack;
unsigned int _E_MSP;

//PROCESS STACK TASKA
unsigned int _S_PSP_TA;
unsigned int _E_PSP_TA;

//PROCESS STACK TASKB
unsigned int _S_PSP_TB;
unsigned int _E_PSP_TB;

uint8_t TaskA_flag, TaskB_flag, Flag = 0;

enum privilege_level {unprivileged, privileged};
void CPU_Access_Level(enum privilege_level lvl)
{
	switch(lvl)
	{
	case unprivileged:
		__asm("mrs r3, CONTROL \n\t"
				"orr r3, r3, #0x1 \n\t"
				"msr CONTROL,r3");
		break;
	case privileged:
		__asm("mrs r3, CONTROL \n\t"
				"lsr r3, r3, #0x1 \n\t"
				"lsl r3, r3, #0x1 \n\t"
				"msr CONTROL,r3");
		break;
	}
}

