void HardFault_Handler()
{

}

__attribute ((naked)) void SVC_Handler()
{
	__asm("tst lr, #4	\n\t"
			"ITE EQ	\n\t"
			"mrseq r0, MSP	\n\t"
			"mrsne r0, PSP	\n\t"
			"B OS_SVC");
}

void OS_SVC(int* StackFrame)
{

	//r0 r1 r2 r3 r12 LR, return address (PC) and XPSR
	unsigned char SVC_NO;
	SVC_NO = *((unsigned char*)(((unsigned char*)StackFrame[6])-2));
	unsigned int val1 = StackFrame[0];
	unsigned int val2 = StackFrame[1];

	switch(SVC_NO)
	{
	case 0: //OS ADD
		StackFrame[0]= val1+val2;
		break;
	case 1: //OS SUB
		StackFrame[0]= val1-val2;
		break;
	case 2: //OS MUL
		StackFrame[0]= val1*val2;
		break;
	case 3: //OS PendSV
			SCB->ICSR |= SCB_ICSR_PENDSVSET_Msk;
			break;
	}

}

void wait (int x)
{
	unsigned int i , j ;
	for (i= 0 ; i<x ; i++)
		for (j= 0 ; j<255 ; j++);
}


void EXTI9_CALLBACK(void)
{
	if (Flag == 0)
	{
		TaskA_flag =1;
		Flag = 1;
	}
	else if (Flag == 1)
	{
		TaskB_flag =1;
		Flag = 0;
	}

}

int TaskA (int a, int b, int c)
{
	return a+b+c;
}

int TaskB (int a, int b,int c, int d)
{
	return a+b+c+d;
}

int OS_SVC_SET(int a, int b, int SVC_ID)
{
	int result;
	switch(SVC_ID)
	{
	case 0: //OS ADD
		__asm("svc #0x00");
		break;
	case 1: //OS SUB
		__asm("svc #0x01");
		break;
	case 2: //OS MUL
		__asm("svc #0x02");
		break;
	case 3: //PendSV
		__asm("svc #0x03");
		break;
	}
	__asm ("mov %0, r0	\n\t" : "=r" (result));
	return result;
}

void MainOs()
{
	_E_MSP = (_S_MSP - 512);

	_S_PSP_TA = (_E_MSP - 8);
	_E_PSP_TA = (_S_PSP_TA - TaskA_stack_sz);

	_S_PSP_TB = (_E_PSP_TA - 8);
	_E_PSP_TB = (_S_PSP_TB - TaskB_stack_sz);

	while(1)
	{
		__asm("nop \n\t");
		if (TaskA_flag == 1)
		{
			//Set PSP register to _S_PSP_TA
			OS_SET_PSP(_S_PSP_TA);

			//SP -> PSP
			OS_SWITCH_SP_TO_PSP;

			//Switch from privileged to unprivileged
			CPU_Access_Level(unprivileged);
			TaskA_flag =TaskA(1,2,3);

			//Switch from unprivileged to privileged
			OS_GENERATE_EXCEPTION;
			//SP -> MSP
			OS_SWITCH_SP_TO_MSP;
		}
		if (TaskB_flag == 1)
		{
			//Set PSP register to _S_PSP_TB
			OS_SET_PSP(_S_PSP_TB);

			//SP -> PSP
			OS_SWITCH_SP_TO_PSP;

			//Switch from privileged to unprivileged
			CPU_Access_Level(unprivileged);
			TaskB_flag =TaskB(1,2,3,4);

			//Switch from unprivileged to privileged
			OS_GENERATE_EXCEPTION;
			//SP -> MSP
			OS_SWITCH_SP_TO_MSP;
		}
	}
}

